import React from 'react';

import $ from 'jquery';

import styled from "styled-components";

import BunnyPriceImg from "../../assets/images/bunny-price.svg";


const HeaderBunnyPrice = styled.div`
    max-width: 1200px;
    width: 100%;
    margin: 0 auto;
    padding: 0 24px;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
`;

const BunnyPrice = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    font-size: 16px;
    font-weight: 700;
    color: #4ed8de;
    text-shadow: 0 0 12px #4ed8de;
    margin-right: 16px;
    cursor: pointer;
      img {
    width: auto;
    height: 26px;
    margin-right: 4px;
    transition: all .2s ease-in-out;
  }

`;

export class Header extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            price: 0.00,
            lastFetch: "" 
        }
    }
    render() {
        return (
            <HeaderBunnyPrice>
                <BunnyPrice><img src={BunnyPriceImg} alt="!" />        <h1>
                    BTC Price: {this.state.price}
                    <small>
                        {this.state.lastFetch}
                    </small>
                </h1></BunnyPrice>
            </HeaderBunnyPrice>
        );
    }

    componentDidMount() {
        this.fetch();
    }
    fetch() {
        var context = this;

        window.setTimeout(function () {
            $.ajax({
                url: "https://api.coindesk.com/v1/bpi/currentprice.json",
                dataType: "json",
                method: "GET",
                success: function (response) {
                    context.setState({
                        price: response.bpi.USD.rate,
                    });
                }
            });
        }, 3000);
    }


}
