import React from 'react';

import styled from "styled-components";



import BunnyPriceImg from "../../assets/images/bunny-price.svg";


const HeaderBunnyPrice = styled.div`
    max-width: 1200px;
    width: 100%;
    margin: 0 auto;
    padding: 0 24px;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
`;

const BunnyPrice = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    font-size: 16px;
    font-weight: 700;
    color: #4ed8de;
    text-shadow: 0 0 12px #4ed8de;
    margin-right: 16px;
    cursor: pointer;
      img {
    width: auto;
    height: 26px;
    margin-right: 4px;
    transition: all .2s ease-in-out;
  }

`;

export class Header extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            price: 0.00,

        }
    }
    render() {
        return (
            <HeaderBunnyPrice>
                <BunnyPrice><img src={BunnyPriceImg} alt="!" />        <h1>
                    Bunny Price: {this.state.price}
                </h1></BunnyPrice>
            </HeaderBunnyPrice>
        );
    }

    componentDidMount() {
        fetch("https://api.coingecko.com/api/v3/simple/price?ids=pancake-bunny&vs_currencies=usd.json")
            .then(res => res.json())
            .then(json => {
                this.setState({
                    items: json,
                    isLoaded: true,
                })
            }).catch((err) => {
                console.log(err);
            });
    }




}
